import numpy as np
import matplotlib.pyplot as plt
from scipy.stats import t, wilcoxon, shapiro, mannwhitneyu


def compute_diffs(iw_matrix, log_reg_matrix):
    # order of subtraction matters for the t-test! -> right-tail
    diffs_matrix = np.subtract(iw_matrix, log_reg_matrix)
    return diffs_matrix


def corrected_resampled_t_test(diffs, n_test, n_train, m):
    A_avg = np.mean(diffs)
    extra_factor = np.sqrt((1.0 / m) + (n_test / n_train))
    std = np.std(diffs, ddof=1)
    t_stat = A_avg / (extra_factor * std)
    return t_stat


def obtain_p_from_t_test(t_stat, dof):
    # p-value for right-tail test
    p_val = t.sf(t_stat, dof)
    return p_val


def remove_outliers(data):
    data = np.array(data)
    Q1 = np.quantile(data, 0.25)
    Q3 = np.quantile(data, 0.75)
    IQR = Q3 - Q1
    lower = Q1 - 1.5 * IQR
    upper = Q3 + 1.5 * IQR
    data = data[data >= lower]
    data = data[data <= upper]
    df = len(data)
    return data, df


